import React, { useEffect, useState } from "react";
import { Routes, Route } from "react-router-dom";
import axios from "axios";
import "../css/App.css";
import Footer from "./Footer";
import Navbar from "./NavBar";
import MainPage from "./MainPage";
import LoginForm from "./LoginForm";
import BoardList from "./BoardList";
import DetailPage from "./DetailPage";
import CreateForm from "./CreateForm";
import UpdateForm from "./UpdateForm";

function App() {
  const [detailBoard, setDetailBoard] = useState("");
  const [page, setPage] = useState(1);

  const pageChangeHandler = (value) => {
    setPage(value);
    axios
      .get(`/api/board/list/paging?page=${page - 1}`)
      .then((res) => {
        setList(res.data.content);
        setCount(res.data.content.length);
      })
      .catch((err) => {
        console.log(err);
      });
    window.location.href = `/board/list?page=${page}`;
  };
  /** 글 상세보기 값 변경(data === id)*/
  const getDetailBoard = (id) => {
    setDetailBoard(id);
  };

  useEffect(() => {}, []);
  return (
    <>
      {/* 네비게이션 */}
      <Navbar />

      <Routes>
        {/* 메인 페이지*/}
        <Route path="/" exact element={<MainPage />} />

        {/* 프로젝트 리스트 */}
        <Route
          path="/board/list"
          exact
          element={
            <BoardList
              exact
              page={page}
              pageChangeHandler={pageChangeHandler}
            />
          }
        />

        {/* 글 생성 페이지 */}
        <Route path="/create" exact element={<CreateForm />} />

        {/* 글 상세 페이지 */}
        <Route
          path="/detail/:id"
          exact
          element={<DetailPage detailBoard={detailBoard} />}
        />

        {/* 글 수정 페이지 */}
        <Route path="/update/:id" exact element={<UpdateForm />} />

        {/* 로그인 화면*/}
        <Route path="/login" exact element={<LoginForm />} />
      </Routes>
      {/* Footer */}
      <Footer />
    </>
  );
}

export default App;
