import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

function UpdateForm(props) {
  let { id } = useParams();

  const [title, setTitle] = useState(props.detailBoard.title);
  const [skillStack, setSkillStack] = useState(props.detailBoard.skill_stack);
  const [workField, setWorkField] = useState(props.detailBoard.work_field);
  const [maxPeople, setMaxPeople] = useState(props.detailBoard.max_people);
  const [endDate, setEndDate] = useState(props.detailBoard.end_date);
  const [content, setContent] = useState(props.detailBoard.content);
  const submitHandler = (e) => {
    const params = new URLSearchParams();

    params.append("title", props.detailBoard.title);
    axios.post("/api/board/modify", params);
  };
  /** 제목 값 변경 */
  const onChangeTitle = useCallback((e) => {
    setTitle(e.target.value);
  }, []);

  const onChagneContent = useCallback((e) => {
    setContent(e.target.value);
  });

  useEffect(() => {
    // 글 내용 불러오기
    axios.get(`/api/board/detail/${id}`).then((res) => {
      props.getDetailBoard(res.data);
    });
  }, []);

  return (
    <>
      <div class="default_form">
        <form onSubmit={submitHandler}>
          <ul>
            <li>
              제목
              <input
                type="text"
                name="title"
                value={title}
                onChange={onChangeTitle}
              />
            </li>
            <li>
              사용 언어
              <input
                type="text"
                name="skill_stack"
                value={props.detailBoard.skill_stack}
              />
            </li>
            <li>
              분야
              <input
                type="text"
                name="work_field"
                value={props.detailBoard.work_field}
              />
            </li>
            <li>
              모집 인원
              <input
                type="text"
                name="max_people"
                value={props.detailBoard.max_people}
              />
            </li>
            <li>
              마감일
              <input
                type="text"
                name="end_date"
                value={props.detailBoard.end_date}
              />
            </li>
          </ul>
          <textarea
            class="create_board"
            name="content"
            value={props.detailBoard.content}
          ></textarea>
          <input type="submit" />
        </form>
      </div>
    </>
  );
}

export default UpdateForm;
