import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import Pagination from "react-js-pagination";
import axios from "axios";
import "../css/board-list.css";

function BoardList() {
  const [posts, setPosts] = useState([]);
  const [count, setCount] = useState(0);
  const [currentPosts, setCurrentPosts] = useState([]);
  const [page, setPage] = useState(1); // 현재 페이지
  const [postPerPages, setPostPerPages] = useState(20);
  const indexOfLastPost = page * postPerPages;
  const indexOfFirstPost = indexOfLastPost - postPerPages;

  const pageChange = (page) => {
    setPage(page);
  };

  // const pageChangeHandler = async (page) => {
  //   // window.location.replace(`/board/list?page=${page}`);
  //   setPage(page);
  //   await axios
  //     .get("/api/board/list")
  //     .then((res) => {
  //       setList(res.data.slice((page - 1) * 20, (page - 1) * 20 + 20));
  //       setCount(res.data.length);
  //     })
  //     .catch((err) => {
  //       console.log(err);
  //     });
  // };
  const pageChangeHandler = async (page) => {
    // window.location.replace(`/board/list?page=${page}`);
    setPage(page);
    // await axios
    //   .get("/api/board/list")
    //   .then((res) => {
    //     setList(res.data);
    //     setCount(list.slice(indexOfFirstPost, indexOfLastPost));
    //   })
    //   .catch((err) => {
    //     console.log(err);
    //   });
  };
  /** 초기 상태 */
  // const getList = async () => {
  //   await axios
  //     .get("/api/board/list")
  //     .then((res) => {
  //       setList(res.data.slice(0, 20));
  //       setCount(res.data.length);
  //     })
  //     .catch((err) => {
  //       console.log(err);
  //     });
  // };
  // axios
  //   .get("/api/board/list")
  //   .then((res) => {
  //     setList(res.data.slice((page - 1) * 20, (page - 1) * 20 + 20));
  //     setCount(res.data.length);
  //   })
  //   .catch((err) => {
  //     console.log(err);
  //   });
  useEffect(() => {
    axios.get("api/board/list").then((res) => {
      setList(res.data);
      setCurrentPosts(posts.slice(indexOfFirstPost, indexOfLastPost));
    });
  }, [indexOfFirstPost, indexOfLastPost]);
  return (
    <>
      <table class="board_list_table">
        {currentPosts.map(function (list, idx) {
          return (
            <>
              <tr class="board_list">
                <td>
                  <Link
                    to={`/detail/${list[idx].id}`}
                    onClick={() => {
                      axios
                        .get(`/api/board/detail/${list[idx].id}`)
                        .then((res) => {
                          console.log(res.data);
                        });
                    }}
                  >
                    {list[idx].title}
                  </Link>
                  <td>아이디 : {list[idx].id} ---</td>
                  <td>스킬{list[idx].skillStack}</td>
                  <td>분야{list[idx].workField}</td>
                  <td>{list[idx].maxPeople}</td>
                </td>
              </tr>
            </>
          );
        })}
      </table>
      <Paging
        totalCount={posts.length}
        postPerPage={postPerPages}
        pageRangeDisplayed={5}
        handlePageChange={handlePageChange}
        page={page}
      />

      <Pagination
        className="pagination"
        activePage={page}
        itemsCountPerPage={20}
        totalItemsCount={count}
        pageRangeDisplayed={5}
        prevPageText={"<"}
        nextPageText={">"}
        onChange={pageChangeHandler}
      />
    </>
  );
}
export default BoardList;
