import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import Pagination from "react-js-pagination";
import axios from "axios";
import "../css/board-list.css";

function BoardList() {
  const [list, setList] = useState([]);
  const [count, setCount] = useState(0);
  const [page, setPage] = useState(1); // 현재 페이지
  const [postPerPage, setPostPerPage] = useState(20);
  const indexOfLastPost = page * postPerPage;
  const indexOfFirstPost = indexOfLastPost - postPerPage;

  const pageChange = (page) => {
    setPage(page);
  };

  // const pageChangeHandler = async (page) => {
  //   // window.location.replace(`/board/list?page=${page}`);
  //   setPage(page);
  //   await axios
  //     .get("/api/board/list")
  //     .then((res) => {
  //       setList(res.data.slice((page - 1) * 20, (page - 1) * 20 + 20));
  //       setCount(res.data.length);
  //     })
  //     .catch((err) => {
  //       console.log(err);
  //     });
  // };
  const pageChangeHandler = async (page) => {
    // window.location.replace(`/board/list?page=${page}`);
    setPage(page);
    await axios
      .get("/api/board/list")
      .then((res) => {
        setList(res.data);
        setCount(list.slice(indexOfFirstPost, indexOfLastPost));
      })
      .catch((err) => {
        console.log(err);
      });
  };
  /** 초기 상태 */
  const getList = async () => {
    // await axios
    //   .get("/api/board/list")
    //   .then((res) => {
    //     setList(res.data.slice(0, 20));
    //     setCount(res.data.length);
    //   })
    //   .catch((err) => {
    //     console.log(err);
    //   });
  };
  axios
    .get("/api/board/list")
    .then((res) => {
      setList(res.data.slice((page - 1) * 20, (page - 1) * 20 + 20));
      setCount(res.data.length);
    })
    .catch((err) => {
      console.log(err);
    });
  useEffect(() => {
    // getList();
  }, []);
  return (
    <>
      <table class="board_list_table">
        {list.map(function (a, i) {
          return (
            <>
              <tr class="board_list">
                <td>
                  <Link
                    to={`/detail/${list[i].id}`}
                    onClick={() => {
                      axios
                        .get(`/api/board/detail/${list[i].id}`)
                        .then((res) => {
                          console.log(res.data);
                        });
                    }}
                  >
                    {list[i].title}
                  </Link>
                  <td>아이디 : {list[i].id} ---</td>
                  <td>스킬{list[i].skillStack}</td>
                  <td>분야{list[i].workField}</td>
                  <td>{list[i].maxPeople}</td>
                </td>
              </tr>
            </>
          );
        })}
      </table>
      <Pagination
        className="pagination"
        activePage={page}
        itemsCountPerPage={20}
        totalItemsCount={count}
        pageRangeDisplayed={5}
        prevPageText={"<"}
        nextPageText={">"}
        onChange={pageChangeHandler}
      />
    </>
  );
}
export default BoardList;
