import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import Pagination from "react-js-pagination";
import axios from "axios";
import "../css/board-list.css";

function BoardList({ getDetailBoard }) {
  const [list, setList] = useState([]);
  const [page, setPage] = useState(1);
  const [totalCnt, setTotalCnt] = useState(0);

  const getListData = async (choice, search, page) => {
    await axios.get("/api/board/list", {
      params: { " choice": choice, search: search, page: page }.then((res) => {
        setList(res.data.List);
        setTotalCnt = res.data.PageCnt;
      }),
    });
  };
  const changePage = (page) => {
    setPage(page);
    getListData();
  };

  useEffect(() => {
    axios
      .get("/api/board/list")
      .then((res) => {
        setList(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);
  return (
    <>
      <table class="board_list_table">
        {list.map(function (a, i) {
          return (
            <>
              <tr class="board_list">
                <td>
                  <Link
                    to={`/detail/${list[i].id}`}
                    onClick={() => {
                      axios
                        .get(`/api/board/detail/${list[i].id}`)
                        .then((res) => {
                          getDetailBoard(res.data);
                        });
                    }}
                  >
                    {list[i].title}
                  </Link>
                  <td>아이디 : {list[i].id} ---</td>
                  <td>스킬{list[i].skillStack}</td>
                  <td>분야{list[i].workField}</td>
                </td>
              </tr>
            </>
          );
        })}
      </table>
      <Pagination
        className="pagination"
        activePage={page}
        itemsCountPerPage={10}
        totalItemsCount={totalCnt}
        pageRangeDisplayed={5}
        prevPageText={"<"}
        nextPageText={">"}
        onChange={""}
      />
    </>
  );
}
export default BoardList;
