import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import Pagination from "react-js-pagination";
import axios from "axios";
import "../css/board-list.css";

function BoardList({ getDetailBoard }) {
  const [list, setList] = useState([]);
  const [page, setPage] = useState(1);
  const [count, setCount] = useState("");
  const pageChangeHandler = (page) => {
    setPage(page);
  };
  const getList = async () => {
    await axios
      .get("/api/board/list")
      .then((res) => {
        setList(res.data);
        setCount(res.data.length);
        console.log(res.data.length);
      })
      .catch((err) => {
        console.log(err);
      });
  };
  useEffect(() => {
    // getList();
    axios
      .get("/api/board/list")
      .then((res) => {
        setList(res.data);
        setCount(res.data.length);
        console.log(res.data.length);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);
  return (
    <>
      <table class="board_list_table">
        {list.map(function (a, i) {
          return (
            <>
              <tr class="board_list">
                <td>
                  <Link
                    to={`/detail/${list[i].id}`}
                    onClick={() => {
                      axios
                        .get(`/api/board/detail/${list[i].id}`)
                        .then((res) => {
                          getDetailBoard(res.data);
                        });
                    }}
                  >
                    {list[i].title}
                  </Link>
                  <td>아이디 : {list[i].id} ---</td>
                  <td>스킬{list[i].skillStack}</td>
                  <td>분야{list[i].workField}</td>
                  <td>{list[i].maxPeople}</td>
                </td>
              </tr>
            </>
          );
        })}
      </table>
      <Pagination
        className="pagination"
        activePage={page}
        itemsCountPerPage={20}
        totalItemsCount={count}
        pageRangeDisplayed={5}
        prevPageText={"<"}
        nextPageText={">"}
        onChange={pageChangeHandler}
      />
    </>
  );
}
export default BoardList;
