import { useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import axios from "axios";

function DetailPage(props) {
  let { id } = useParams();

  useEffect(() => {
    // 글 내용 불러오기
    axios.get(`/api/board/detail/${id}`).then((res) => {
      props.getDetailBoard(res.data);
    });
  }, []);
  return (
    <>
      <ul>
        <li>타이틀 {props.detailBoard.title}</li>
        <li>이름 {props.detailBoard.name}</li>
        <li>작성일 {props.detailBoard.create_date}</li>
        <li>사용 언어 {props.detailBoard.skill_stack}</li>
        <li>분야 {props.detailBoard.work_field}</li>
        <li>정원 {props.detailBoard.max_people}</li>
        <li>예상 기간 {props.detailBoard.end_date}</li>
        <li>내용 {props.detailBoard.content}</li>
      </ul>
      아이디{props.detailBoard.id}
      이름{props.detailBoard.name}
      타이틀{props.detailBoard.title}
      스킬{props.detailBoard.skill_stack}
      분야{props.detailBoard.work_field}
      정원{props.detailBoard.max_people}
      마감일{props.detailBoard.end_date}
      내용{props.detailBoard.content}
      작성일{props.detailBoard.create_date}
      <div>
        <button>
          <Link to={`/update/${id}`}>수정</Link>
        </button>
        <button
          onClick={() => {
            axios.get(`/api/board/delete?id=${id}`);
            alert("삭제 완료");
            window.location.href = "/";
          }}
        >
          삭제
        </button>
      </div>
    </>
  );
}

export default DetailPage;
