import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

function UpdateForm(props) {
  let { id } = useParams();

  const [title, setTitle] = useState(props.getDetailBoard.title);
  const [skillStack, setSkillStack] = useState(
    props.getDetailBoard.skill_stack
  );
  const [workField, setWorkField] = useState(props.getDetailBoard.work_field);
  const [maxPeople, setMaxPeople] = useState(props.getDetailBoard.max_people);
  const [endDate, setEndDate] = useState(props.getDetailBoard.end_date);
  const [content, setContent] = useState(props.getDetailBoard.content);

  useEffect(() => {
    // 글 내용 불러오기
    axios
      .get(`/api/board/detail/${props.getDetailBoard.work_field.id}`)
      .then((res) => {
        props.getDetailBoard(res.data);
      });
  }, []);

  return (
    <>
      <div>
        <input name="id" value={props.detailBoard.id} />
      </div>
    </>
  );
}

export default UpdateForm;
