import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

function UpdateForm(props) {
  let { id } = useParams();
  console.log(props);
  const [title, setTitle] = useState(props.title);
  const [skillStack, setSkillStack] = useState(props.skill_stack);
  const [workField, setWorkField] = useState(props.work_field);
  const [maxPeople, setMaxPeople] = useState(props.max_people);
  const [endDate, setEndDate] = useState(props.end_date);
  const [content, setContent] = useState(props.detailBoard.content);

  const submitHandler = (e) => {
    const params = new URLSearchParams();
    params.append("title", title);
    params.append("skill_stack", skillStack);
    params.append("work_field", workField);
    params.append("max_people", maxPeople);
    params.append("end_date", endDate);
    params.append("content", content);
    alert("수정 완료!");
    window.location.href = "/board/list";
    axios.post(`/api/board/update/${id}`, params);
  };
  /** 제목 값 변경 */
  const onChangeTitle = useCallback((e) => {
    setTitle(e.target.value);
  }, []);

  const onChangeSkillStack = useCallback((e) => {
    setSkillStack(e.target.value);
  }, []);

  const onChangeWorkField = useCallback((e) => {
    setWorkField(e.target.value);
  }, []);

  const onChangeMaxPeople = useCallback((e) => {
    setMaxPeople(e.target.value);
  }, []);

  const onChangeEndDate = useCallback((e) => {
    setEndDate(e.target.value);
  }, []);

  const onChangeContent = useCallback((e) => {
    setContent(e.target.value);
  }, []);
  /** 셀렉트의 옵션을 선택하게 하는 함수
   * 셀렉트를 전체 선택 후 selectedValue안의 값으로 selected한다.
   */
  const selectOption = (e) => {
    var opt = document.querySelectorAll(".select");
    var selectedValue = [maxPeople, endDate];
    for (var i = 0; i < opt.length; i++) {
      for (var j = 0; j < opt[i].length; j++) {
        if (opt[i][j].value == selectedValue[i]) {
          opt[i][j].setAttribute("selected", true);
          break;
        }
      }
    }
  };
  selectOption();

  useEffect(() => {
    // 글 내용 불러오기
    axios.get(`/api/board/detail/${id}`).then((res) => {
      props.getDetailBoard(res.data);
      setTitle(res.data.title);
      setSkillStack(res.data.skill_stack);
      setWorkField(res.data.work_field);
      setMaxPeople(res.data.max_people);
      setEndDate(res.data.end_date);
      setContent(res.data.content);
    });
  }, []);

  return (
    <>
      <div class="default_form">
        <form onSubmit={submitHandler}>
          <ul>
            <li>
              제목
              <input
                type="text"
                name="title"
                value={title}
                onChange={onChangeTitle}
              />
            </li>
            <li>
              사용 언어
              <input
                type="text"
                name="skill_stack"
                value={skillStack}
                onChange={onChangeSkillStack}
              />
            </li>
            <li>
              분야
              <input
                type="text"
                name="work_field"
                value={workField}
                onChange={onChangeWorkField}
              />
            </li>
            <li>
              <label for="max_people">모집 인원</label>
              <select
                class="select"
                name="max_people"
                onChange={onChangeMaxPeople}
              >
                <option value="1명">1명</option>
                <option value="2명">2명</option>
                <option value="3명">3명</option>
                <option value="4명">4명</option>
                <option value="5명">5명</option>
                <option value="6명">6명</option>
                <option value="7명">7명</option>
                <option value="8명">8명 이상</option>
              </select>
            </li>
            <li>
              <label for="end_date">예상 기간</label>
              <select class="select" name="end_date" onChange={onChangeEndDate}>
                <option value="1개월">1개월</option>
                <option value="2개월">2개월</option>
                <option value="3개월">3개월</option>
                <option value="4개월">4개월</option>
                <option value="5개월">5개월</option>
                <option value="6개월 이상">6개월 이상</option>
              </select>
            </li>
          </ul>
          <textarea
            class="create_board"
            name="content"
            value={content}
            onChange={onChangeContent}
          ></textarea>
          <input type="submit" />
        </form>
      </div>
    </>
  );
}

export default UpdateForm;
