import React, { useEffect, useState } from "react";
import DetailPage from "./DetailPage";
import { Route, Link } from "react-router-dom";
import axios from "axios";

function BoardList({ getBoard, board }) {
  const [list, setList] = useState([]);
  useEffect(() => {
    axios.get("/api/board/list").then((res) => {
      setList(res.data);
    });
  }, []);
  return (
    <>
      <table>
        {list.map(function (a, i) {
          return (
            <>
              <tr>
                <td>{list[i].id}</td>
                <td>
                  <Link
                    to={`/detail/${list[i].id}`}
                    onClick={() => {
                      axios
                        .get(`/api/board/detail/${list[i].id}`)
                        .then((res) => {
                          getBoard(res.data);
                        });
                    }}
                  >
                    {list[i].title}
                  </Link>
                </td>
              </tr>
            </>
          );
        })}
      </table>
      <Route path="/detail/:id" element={<DetailPage board={board} />} />
    </>
  );
}
export default BoardList;
