import React, { useState, useCallback } from "react";
import axios from "axios";
import "../css/board-create.css";

function CreateForm() {
  const [title, setTitle] = useState("");
  const [skillStack, setSkillStack] = useState("");
  const [workField, setWorkField] = useState("");
  const [maxPeople, setMaxPeople] = useState("1명");
  const [endDate, setEndDate] = useState("1개월");
  const [content, setContent] = useState("");

  const createSubmitHandler = (e) => {
    window.location.href = "/board/list";
  };
  const onChangeTitle = useCallback((e) => {
    setTitle(e.target.value);
  }, []);

  const onChangeSkillStack = useCallback((e) => {
    setSkillStack(e.target.value);
  }, []);

  const onChangeWorkField = useCallback((e) => {
    setWorkField(e.target.value);
  }, []);

  const onChangeMaxPeople = useCallback((e) => {
    setMaxPeople(e.target.value);
  }, []);

  const onChangeEndDate = useCallback((e) => {
    setEndDate(e.target.value);
  }, []);

  const onChangeContent = useCallback((e) => {
    setContent(e.target.value);
  }, []);

  const submitHandler = (e) => {
    const params = new URLSearchParams();
    params.append("title", title);
    params.append("skill_stack", skillStack);
    params.append("work_field", workField);
    params.append("max_people", maxPeople);
    params.append("end_date", endDate);
    params.append("content", content);
    alert("글 작성 완료!");
    window.location.href = "/board/list";
    axios.post(`/api/board/create`, params);
  };

  return (
    <div class="default_form">
      <form method="post" action="/api/board/create" onSubmit={submitHandler}>
        <ul>
          <li>
            제목
            <input type="text" name="title" onChange={onChangeTitle} />
          </li>
          <li>
            사용 언어
            <input
              type="text"
              name="skill_stack"
              onChange={onChangeSkillStack}
            />
          </li>
          <li>
            분야
            <input type="text" name="work_field" onChange={onChangeWorkField} />
          </li>
          <li>
            <label for="max_people">모집 인원</label>
            <select name="max_people" onChange={onChangeMaxPeople}>
              <option value="1명">1명</option>
              <option value="2명">2명</option>
              <option value="3명">3명</option>
              <option value="4명">4명</option>
              <option value="5명">5명</option>
              <option value="6명">6명</option>
              <option value="7명">7명</option>
              <option value="8명">8명 이상</option>
            </select>
          </li>
          <li>
            <label for="end_date">예상 기간</label>
            <select name="end_date" onChange={onChangeEndDate}>
              <option value="1개월">1개월</option>
              <option value="2개월">2개월</option>
              <option value="3개월">3개월</option>
              <option value="4개월">4개월</option>
              <option value="5개월">5개월</option>
              <option value="6개월 이상">6개월 이상</option>
            </select>
          </li>
        </ul>
        <textarea
          class="create_board"
          name="content"
          onChange={onChangeContent}
        ></textarea>
        <input class="submit" type="submit" value="저장" />
      </form>
    </div>
  );
}

export default CreateForm;
