import React, { useEffect, useState, useRef } from "react";
import { Link } from "react-router-dom";
import Pagination from "react-js-pagination";
import axios from "axios";
import "../css/board-list.css";

function BoardList() {
  const [totalList, setTotalList] = useState([]);
  const [list, setList] = useState([]);
  const [count, setCount] = useState(0);
  const [page, setPage] = useState(1);
  const [fixSearch, setFixSearch] = useState("");
  const [search, setSearch] = useState("");

  /** 검색한 경우 fixSearch 값 변경*/
  const keyWordChange = (e) => {
    e.preventDefault();
    window.scrollTo(0, 0);
    setFixSearch(search);
    // homeRef.current?.scrollIntoView({ behavior: "smooth" });
  };
  /** 페이지 이동 */
  const pageChangeHandler = async (page) => {
    window.scrollTo(0, 0);
    setPage(page);
    setList(totalList.slice((page - 1) * 20, page * 20));
    // window.location.replace(`/board/list?page=${page}`);
    // window.location.reload();
  };
  /** 초기 상태 */
  const getList = async () => {
    await axios
      .get("/api/board/list")
      .then((res) => {
        setTotalList(res.data);
        setList(res.data.slice(0, 20));
        setCount(res.data.length);
      })
      .catch((err) => {
        console.log(err);
      });
  };
  const searchList = async (keyword) => {
    await axios
      .get("/api/board/list/search", { params: { title: keyword } })
      .then((res) => {
        setTotalList(res.data);
        setList(res.data.slice(0, 20));
        setCount(res.data.length);
      })
      .catch((err) => {
        console.log(err);
      });
  };
  useEffect(() => {
    setPage(1);
    // 검색한 내용이 있는 경우
    if (fixSearch) {
      searchList(fixSearch);
    }
    // 검색한 내용이 없는 경우
    else {
      getList();
    }
  }, [fixSearch]);
  return (
    <>
      {/* 검색창 */}
      <form id="main_search" onSubmit={keyWordChange}>
        <input
          type="text"
          placeholder="제목을 입력하세요"
          onChange={(e) => {
            setSearch(e.target.value);
          }}
        />
        <input type="submit" value="검색" />
      </form>
      <div class="board_list_div">
        <table class="board_list_table">
          {list.map(function (lists, i) {
            return (
              <>
                <tr
                  class="board_list"
                  onClick={(e) => {
                    axios.get(`/api/board/detail/${list[i].id}`).then((res) => {
                      window.location.href = `/detail/${list[i].id}`;
                    });
                  }}
                >
                  <td>
                    <Link
                      to={`/detail/${list[i].id}`}
                      onClick={() => {
                        axios
                          .get(`/api/board/detail/${list[i].id}`)
                          .then((res) => {
                            console.log(res.data);
                          });
                      }}
                    >
                      {list[i].title}
                    </Link>
                    <td>아이디 : {list[i].id} ---</td>
                    <td>스킬 : {list[i].skillStack}</td>
                    <td>분야 : {list[i].workField}</td>
                    <td>정원 : {list[i].maxPeople}</td>
                  </td>
                </tr>
              </>
            );
          })}
        </table>
        <Pagination
          className="pagination"
          activePage={page}
          itemsCountPerPage={20}
          totalItemsCount={count}
          pageRangeDisplayed={5}
          prevPageText={"<"}
          nextPageText={">"}
          onChange={pageChangeHandler}
        />
      </div>
    </>
  );
}

export default BoardList;
