import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

function UpdateForm(props) {
  let { id } = useParams();

  const [title, setTitle] = useState(props.detailBoard.title);
  const [skillStack, setSkillStack] = useState(props.detailBoard.skill_stack);
  const [workField, setWorkField] = useState(props.detailBoard.work_field);
  const [maxPeople, setMaxPeople] = useState(props.detailBoard.max_people);
  const [endDate, setEndDate] = useState(props.detailBoard.end_date);
  const [content, setContent] = useState(props.detailBoard.content);

  const submitHandler = (e) => {
    const params = new URLSearchParams();
    params.append("title", title);
    params.append("skill_stack", skillStack);
    params.append("work_field", workField);
    params.append("max_people", maxPeople);
    params.append("end_date", endDate);
    params.append("content", content);
    alert("수정 완료!");
    axios.post(`/api/board/update/${id}`, params);
    window.location.href = "http://localhost:8080";
  };
  /** 제목 값 변경 */
  const onChangeTitle = useCallback((e) => {
    setTitle(e.target.value);
  }, []);

  const onChangeSkillStack = useCallback((e) => {
    setSkillStack(e.target.value);
  }, []);

  const onChangeWorkField = useCallback((e) => {
    setWorkField(e.target.value);
  }, []);

  const onChangeMaxPeople = useCallback((e) => {
    setMaxPeople(e.target.value);
  }, []);

  const onChangeEndDate = useCallback((e) => {
    setEndDate(e.target.value);
  }, []);

  const onChangeContent = useCallback((e) => {
    setContent(e.target.value);
  }, []);

  useEffect(() => {
    // 글 내용 불러오기
    axios.get(`/api/board/detail/${id}`).then((res) => {
      props.getDetailBoard(res.data);
    });
  }, []);

  return (
    <>
      <div class="default_form">
        <form onSubmit={submitHandler}>
          <ul>
            <li>
              제목
              <input
                type="text"
                name="title"
                value={title}
                onChange={onChangeTitle}
              />
            </li>
            <li>
              사용 언어
              <input
                type="text"
                name="skill_stack"
                value={skillStack}
                onChange={onChangeSkillStack}
              />
            </li>
            <li>
              분야
              <input
                type="text"
                name="work_field"
                value={workField}
                onChange={onChangeWorkField}
              />
            </li>
            <li>
              모집 인원
              <input
                type="text"
                name="max_people"
                value={maxPeople}
                onChange={onChangeMaxPeople}
              />
            </li>
            <li>
              마감일
              <input
                type="text"
                name="end_date"
                value={endDate}
                onChange={onChangeEndDate}
              />
            </li>
          </ul>
          <textarea
            class="create_board"
            name="content"
            value={content}
            onChange={onChangeContent}
          ></textarea>
          <input type="submit" />
        </form>
      </div>
    </>
  );
}

export default UpdateForm;
