import { useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import "../css/detail-page.css";
import axios from "axios";

function DetailPage() {
  let { id } = useParams();
  const [title, setTitle] = useState("");
  const [name, setName] = useState("");
  const [createDate, setCreateDate] = useState("");
  const [skillStack, setSkillStack] = useState("");
  const [workField, setWorkField] = useState("");
  const [maxPeople, setMaxPeople] = useState("");
  const [endDate, setEndDate] = useState("");
  const [content, setContent] = useState("");
  useEffect(() => {
    // 글 내용 불러오기
    axios.get(`/api/board/detail/${id}`).then((res) => {
      setTitle(res.data.title);
      setName(res.data.name);
      setCreateDate(res.data.createDate.slice(0, 10));
      setSkillStack(res.data.skillStack);
      setWorkField(res.data.workField);
      setMaxPeople(res.data.maxPeople);
      setEndDate(res.data.endDate);
      setContent(res.data.content);
    });
  }, []);
  return (
    <>
      <div class="detail_board">
        <table>
          <tr>
            <td class="title">{title}</td>
          </tr>
          <tr>
            <td class="createDate">
              <b>작성일 </b>
              {createDate}
            </td>
            <td class="author">
              <b>작성자 </b>
              {id}
            </td>
          </tr>
          <tr>
            <td class="workField">
              <b> 분야</b>
              {workField}
            </td>
            <td class="skillStack">
              <b>사용 언어</b> {skillStack}
            </td>
          </tr>
          <tr>
            <td class="maxPeople">
              <b>모집 인원</b> {maxPeople}
            </td>
            <td>
              <b>예상 기간 </b>
              {endDate}
            </td>
          </tr>
          <tr class="content">
            <td>{content}</td>
          </tr>
        </table>
        <div>
          <button>
            <Link to={`/update/${id}`}>수정</Link>
          </button>
          <button
            onClick={() => {
              axios
                .get(`/api/board/delete?id=${id}`)
                .then((res) => {
                  window.location.href = "/";
                  alert("삭제 완료");
                })
                .catch((err) => {
                  console.log(err);
                });
            }}
          >
            삭제
          </button>
        </div>
        <div>
          <textarea></textarea>
          <button>댓글 등록</button>
        </div>
      </div>
    </>
  );
}

export default DetailPage;
