import React, { useEffect, useState } from "react";
import { Routes, Route, Link } from "react-router-dom";
import axios from "axios";
import Pagination from "react-js-pagination";
import "../css/board-list.css";

function BoardList({ getDetailBoard }) {
  const [list, setList] = useState([]);
  const [page, setPage] = useState(1);

  const handlePageChange = (page) => {
    setPage(page);
  };

  useEffect(() => {
    axios.get("/api/board/list").then((res) => {
      setList(res.data);
    });
  }, []);
  return (
    <>
      <table class="board_list_table">
        {list.map(function (a, i) {
          return (
            <>
              <tr class="board_list">
                <td>
                  <Link
                    to={`/detail/${list[i].id}`}
                    onClick={() => {
                      axios
                        .get(`/api/board/detail/${list[i].id}`)
                        .then((res) => {
                          getDetailBoard(res.data);
                        });
                    }}
                  >
                    {list[i].title}
                  </Link>
                  <td>{list[i].skill_stack}</td>
                  <td>{list[i].work_field}</td>
                  <td>{list[i].max_people}</td>
                  <td>{list[i].name}</td>
                </td>
              </tr>
            </>
          );
        })}
      </table>
      <Pagination
        activePage={page}
        itemsCountPerPage={10}
        totalItemsCount={list.length}
        pageRangeDisplayes={5}
        prevPageText={"<"}
        nextPageText={">"}
        onChange={handlePageChange}
      />
    </>
  );
}
export default BoardList;
