import { useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import "../css/detail-page.css";
import axios from "axios";

function DetailPage() {
  let { id } = useParams();
  const [title, setTitle] = useState("");
  const [name, setName] = useState("");
  const [createDate, setCreateDate] = useState("");
  const [skillStack, setSkillStack] = useState("");
  const [workField, setWorkField] = useState("");
  const [maxPeople, setMaxPeople] = useState("");
  const [endDate, setEndDate] = useState("");
  const [content, setContent] = useState("");
  useEffect(() => {
    // 글 내용 불러오기
    axios.get(`/api/board/detail/${id}`).then((res) => {
      setTitle(res.data.title);
      setName(res.data.name);
      setCreateDate(res.data.createDate);
      setSkillStack(res.data.skillStack);
      setWorkField(res.data.workField);
      setMaxPeople(res.data.maxPeople);
      setEndDate(res.data.endDate);
      setContent(res.data.content);
    });
  }, []);
  return (
    <>
      <ul>
        <li>
          <div>타이틀 {title}</div>
        </li>
        <li class="title">작성자 {id}</li>
        <li>이름 {name}</li>
        <li>작성일 {createDate}</li>
        <li>사용 언어 {skillStack}</li>
        <li>분야 {workField}</li>
        <li>정원 {maxPeople}</li>
        <li>예상 기간 {endDate}</li>
        <li>내용 {content}</li>
      </ul>
      <div>
        <button>
          <Link to={`/update/${id}`}>수정</Link>
        </button>
        <button
          onClick={() => {
            axios
              .get(`/api/board/delete?id=${id}`)
              .then((res) => {
                window.location.href = "/";
                alert("삭제 완료");
              })
              .catch((err) => {
                console.log(err);
              });
          }}
        >
          삭제
        </button>
      </div>
      <div>
        <textarea></textarea>
        <button>댓글 등록</button>
      </div>
    </>
  );
}

export default DetailPage;
