import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import Pagination from "react-js-pagination";
import axios from "axios";
import "../css/board-list.css";

function BoardList() {
  const [totalList, setTotalList] = useState([]);
  const [list, setList] = useState([]);
  const [count, setCount] = useState(0);
  const [page, setPage] = useState(1);
  const [fixSearch, setFixSearch] = useState("");
  const [search, setSearch] = useState("");
  const [url, setUrl] = useState("");

  /** 검색한 경우 */
  const searchList = (e) => {
    // window.location.reload();
    setFixSearch(search);
    if (fixSearch) {
      setPage(1);
      axios
        .get("/api/board/list/search", {
          params: {
            title: fixSearch,
          },
        })
        .then((res) => {
          setTotalList(res.data);
          setCount(res.data.length);
          console.log(res.data);
        })
        .catch((err) => console.log(err));
      setList(totalList.slice(0, 20));
    } else {
      getList();
    }
    e.preventDefault();
  };
  /** 페이지 이동 */
  const pageChangeHandler = async (page) => {
    setPage(page);
    setList(totalList.slice((page - 1) * 20, page * 20));
    // window.location.replace(`/board/list?page=${page}`);
    // window.location.reload();
  };
  /** 초기 상태 */
  const getList = async () => {
    await axios
      .get("/api/board/list")
      .then((res) => {
        setList(res.data.slice(0, 20));
        setCount(res.data.length);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  useEffect(() => {
    getList();
  }, []);
  return (
    <>
      {/* 검색창 */}
      <form id="main_search" onSubmit={searchList}>
        <input
          type="text"
          onChange={(e) => {
            setSearch(e.target.value);
          }}
        />
        <input type="submit" value="검색" />
      </form>
      <table class="board_list_table">
        {list.map(function (lists, i) {
          return (
            <>
              <tr class="board_list">
                <td>
                  <Link
                    to={`/detail/${list[i].id}`}
                    onClick={() => {
                      axios
                        .get(`/api/board/detail/${list[i].id}`)
                        .then((res) => {
                          console.log(res.data);
                        });
                    }}
                  >
                    {list[i].title}
                  </Link>
                  <td>아이디 : {list[i].id} ---</td>
                  <td>스킬{list[i].skillStack}</td>
                  <td>분야{list[i].workField}</td>
                  <td>{list[i].maxPeople}</td>
                </td>
              </tr>
            </>
          );
        })}
      </table>
      <Pagination
        className="pagination"
        activePage={page}
        itemsCountPerPage={20}
        totalItemsCount={count}
        pageRangeDisplayed={5}
        prevPageText={"<"}
        nextPageText={">"}
        onChange={pageChangeHandler}
      />
    </>
  );
}

export default BoardList;
